import React, {Fragment} from 'react';
import logo from '../logo.png';
import './navbar.css'
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuItem from '@material-ui/core/MenuItem';
import Menu from '@material-ui/core/Menu';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';

import {Link} from 'react-router-dom'

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
  fabButton: {
    position: 'absolute',
    zIndex: 1,
    left: 0,
    right: 0,
    margin: '0 auto',
  },
}));

export default function MenuAppBar() {
  const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar className="toolbar">
            <img src={logo}/>

            {localStorage.getItem("token") ? 
            <Fab color="primary" aria-label="add" className={classes.fabButton}>
                <Link to={'/post/create'} className="nav-link"><AddIcon /></Link>
            </Fab>
            : null }

            <div>
              <IconButton
                aria-label="account of current user"
                aria-controls="menu-appbar"
                aria-haspopup="true"
                onClick={handleMenu}
                color="inherit"
              >
                <AccountCircle />
              </IconButton>
              <Menu
                id="menu-appbar"
                anchorEl={anchorEl}
                anchorOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                keepMounted
                transformOrigin={{
                  vertical: 'top',
                  horizontal: 'right',
                }}
                open={open}
                onClose={handleClose}>
                    {localStorage.getItem("token") ?
                    <Fragment>
                        <MenuItem><Link to={'/me'} className="nav-link">Profil</Link></MenuItem>
                        <MenuItem><Link onClick={() => {localStorage.clear()}} to={'/'} className="nav-link">Déconnexion</Link></MenuItem>
                    </Fragment> 
                   :  
                    <Fragment>
                        <MenuItem><IconButton size="small" color="inherit" >
                            <Link to={'/'} className="nav-link">Connexion</Link>
                        </IconButton></MenuItem>
                        <MenuItem><IconButton size="small" color="inherit" >
                            <Link to={'/signup'} className="nav-link">Inscription</Link>
                        </IconButton></MenuItem>  
                    </Fragment>}

              </Menu>
            </div>

        </Toolbar>
      </AppBar>
    </div>
  );
}
