import React, { Component, Fragment } from 'react'
import { Form, FormGroup, Label, Button, Input, Container } from 'reactstrap';
import articlesAPI from '../../Api/articlesAPI'
import { Redirect } from 'react-router-dom'

const headers = {
    'Authorization': localStorage.getItem('token'),
    'Content-Type': 'multipart/form-data; charset=utf-8; boundary="another cool boundary'
};

class createArticle extends Component {

    state = {
        errors : [],
        content: '',
        redirect: false
    }

    handleSubmit = (event) => {
        event.preventDefault();

        let fileSelected = document.querySelector("#filedoc").files[0] ? document.querySelector("#filedoc").files[0] : null;

        // let data = new FormData(document.getElementById("form"));

        // data.append("content", this.state.content);
        // data.append("attachment", fileSelected);


        // articlesAPI.postArticles(data, headers)
        //     .then(() => {
        //         console.log("Article ajouté")
        //        this.setState({ redirect: true })
        //     })
        //     .catch(err => console.log(err))

        let content = this.state.content
        let attachment = fileSelected

        let order = {content, attachment}

        let StringOrderingInformation = JSON.stringify(order)

        fetch("http://localhost:4000/api/article",{
                    method: 'POST',
                    body: StringOrderingInformation,
                    headers: {'Content-Type': 'multipart/form-data'}
                })
    }

    handleChange = (event) => {
        const { name, value} = event.target
        this.setState({ [name]: value })   
    }

    render () {

        if(this.state.redirect){
            return  <Redirect to='/post'/>
        }

        return (
            <Fragment>
                <Container className="container">
                    <Form onSubmit={this.handleSubmit} id='form'>
                        <FormGroup>
                            <Label for="content">Text</Label>
                            <Input type="textarea" name="content" placeholder="Le contenu du post" required
                            value={this.state.content} onChange={this.handleChange}/>
                            {/* <span style={{color: "red"}}>{this.state.errors["content"]}</span> */}
                        </FormGroup>
                        <FormGroup>
                            <Label for="attachment"></Label>
                            <Input type="file" name="attachment" id="filedoc" />
                            {/* <span style={{color: "red"}}>{this.state.errors["file"]}</span> */}
                        </FormGroup>
                        <Button type="submit" color="secondary" size="lg" block>Publier</Button>
                    </Form>
                </Container>
            </Fragment>
        )
    }
}

export default createArticle
