import React, { Component } from 'react'
import postAPI from '../../Api/postAPI'

const headers = {
    'Authorization': localStorage.getItem('token')
};

class allArticles extends Component {
    state = {
        posts: [],
        isLoaded: false,
        error : null
    }

    componentDidMount(){
        postAPI.getProfile(headers)
        .then(articles => {
            this.setState({
                posts: articles.data,
                isLoaded: true
            }),
            (error) => (
                this.setState({
                    isLoaded: true,
                    error
                  })
            )


        })
    }

    render () {

        return (
            <div>
                <h1>Post</h1>
            </div>
        )
    }
}

export default allArticles
