import React, { Component, Fragment } from 'react'
import articleAPI from '../../Api/articlesAPI'
import Article from './Article/Article';
import CreateArticleForm from './createArticle'
import { Redirect, withRouter } from 'react-router-dom';
import CreateComment from '../Comments/createComment'


class allArticles extends Component {

    state = {
        posts: {},
        isLoaded: false,
        update: false,
        updateArticle: null,
        id: "",
        error : null,
        reqHeader: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        isAdmin: false
    }

    componentDidMount(){
        // console.log(this.props.location)

        articleAPI.getArticles(this.state.reqHeader)
        .then(articles => {
            this.setState({
                posts: articles.data.article,
                isLoaded: true, 
                isAdmin: articles.data.isAdmin })
        })
        .catch(err => console.log(err))

        console.log(this.state.article)
    }

    handleLikeClick = (id) => {
        articleAPI.likeArticle(id, this.state.reqHeader)
        .then((res) => {
            console.log(res)
            let posts = [...this.state.posts]
            let post = posts.find(post => post.id === id)
            if(res.status === 201){
                post.likes += 1
            } else {
                post.likes -= 1
            }
            this.setState({posts})
        })
        .catch((err) => console.log(err))
    }

    handleDeletePost = (id) => {
        articleAPI.deleteArticle(id, this.state.reqHeader)
        .then(() => console.log("Article supprimé"))
        .catch(err => console.log(err))
    }

    handleUpdatePost = (id) => {
        this.setState({update: true, updateArticle: id})
    }

    render () {

        const {posts, update, updateArticle} = this.state


        if(update){
            return <Redirect to={'/post/update/' + updateArticle } />
        }

