import React from 'react'
import Comment_form from '../Form/Comment_form'

const createComment = () => {

    const [value, setValue] = useState("");

    const handleChange = (event) => {
        setValue(event.target.value)
    }

    return (
        <Comment_form 
        commentChange={handleChange}
        commentValue={value}
        />
    )
}

export default createComment
