import React, { Component, Fragment } from 'react'
import API from '../../Api/articlesAPI'
import Article from './Article/Article';

const headers = {
    'Authorization': localStorage.getItem('token')
};

class allArticles extends Component {

    state = {
        posts: {},
        isLoaded: false,
        error : null
    }

    componentDidMount(){
        const queryString =  window.location.href;
        let id = queryString.split('/post/')[1]

        API.getOneArticle(id, headers)
        .then(article => {
            this.setState({
                posts: article.data,
                isLoaded: true
            })              
        })
        .catch(err => console.log(err))
    }

    render () {

        const {posts} = this.state

        const liste = Object.keys(posts)
        .map(id => (
            <Article 
            key={posts[id].id}
            id={posts[id].id}
            profilePicture={posts[id].User.profilePicture}
            name={posts[id].User.name}
            likes={posts[id].likes}
            // date={posts[id].createdAt.}
            content={posts[id].content} 
            attachment={posts[id].attachment}
            />
        ))

        return (
            <Fragment>
                {liste.reverse()}
            </Fragment>
        )
    }
}

export default allArticles
