import React, { Component, Fragment } from 'react'
import { Form, FormGroup, Label, Button, Input, Row, Container, Col } from 'reactstrap';
import userAPI from '../../Api/userAPI'
import {Redirect} from 'react-router-dom'


class Signup extends Component {

    state = {
        // init empty data
        firstname: "",
        lastname: "",
        email: "",
        password: "",
        confirmPassword: "",
        redirection: false
    }

    handleChange = (event) => {
        let { name, value} = event.target
        this.setState({ [name] : value })
    }

    verification = () => {
        const { firstname, lastname, email, password, confirmPassword} = this.state

        if(!/[A-Za-z]{2,}/.test(firstname)){
            return "Le Prénom est invalide"
        }
        if(!/[A-Za-z]{2,}/.test(lastname)){
            return "Le Nom est invalide"
        }
        if(!/^[A-Za-z0-9._-]+@[A-Za-z0-9._-]\.[A-Za-z]{2,}$/.test(email)){
            return "L'Email est invalide"
        }
        if(!password || password.length < 8 ){
            return "Le mot de passe doit avoir au moins 8 caractères"
        }
        if(confirmPassword !== password) {
            return "La confirmation du mot de passe est incorrect"
        }
    }

    handleSubmit = event => {
        event.preventDefault()
        this.verification()
        const { firstname, lastname, email, password} = this.state

        userAPI.signup(firstname, lastname ,email, password)
        .then(() => {
            this.setState({redirection: true})
          }, (error) => {
            console.log(error);
          });
    }

    render () {

        const {redirection} = this.state;
        if(redirection){
            return <Redirect to='/Login'/>;
        }

        return (
            <Fragment>
                <Container>
                <Form onSubmit={this.handleSubmit}>
                        <Row>
                            <Col xs="6">
                                <FormGroup>
                                    <Label for="firstname">Prénom</Label>
                                    <Input type="text" name="firstname"  placeholder="Prénom" required 
                                    value={this.state.firstname} onChange={this.handleChange}/>
                                </FormGroup>
                            </Col>
                            <Col xs="6">
                                <FormGroup>
                                    <Label for="lastname">Nom</Label>
                                    <Input type="text" name="lastname" placeholder="Nom" required 
                                    value={this.state.lastname} onChange={this.handleChange}/>
                                </FormGroup>
                            </Col>
                        </Row>
                        <FormGroup>
                            <Label for="email">Email</Label>
                            <Input type="email" name="email" placeholder="exemple@exemple.com" required 
                            value={this.state.email} onChange={this.handleChange}/>
                        </FormGroup>
                        <FormGroup>
                            <Label for="password">Mot de passe</Label>
                            <Input type="password" name="password" placeholder="Mot de passe" required
                            value={this.state.password} onChange={this.handleChange}/>
                        </FormGroup>
                        <FormGroup>
                            <Label for="corfirmPassword">Mot de passe</Label>
                            <Input type="password" name="confirmPassword" placeholder="Confirmation du mot de passe" required
                            value={this.state.confirmPassword} onChange={this.handleChange}/>
                        </FormGroup>
                        <Button type="submit">S'inscrire</Button>
                    </Form>
                </Container>
            </Fragment>
        )
    }
}

export default Signup
