import React, { Component, Fragment } from 'react'
import articleAPI from '../../Api/articlesAPI'
import commentAPI from '../../Api/commentAPI'
import Article from './Article/Article';
import CreateArticleForm from './createArticle'
import { Redirect } from 'react-router-dom';

const headers = {
    'Authorization': `Bearer ${localStorage.getItem('token')}`
};


class allArticles extends Component {

    state = {
        posts: {},
        isLoaded: false,
        redirect: false,
        id: "",
        error : null
    }

    componentDidMount(){
        articleAPI.getArticles(headers)
        .then(articles => {
            this.setState({
                posts: articles.data,
                isLoaded: true})
        })
        .catch(err => console.log(err))
    }

    handleLikeClick = (id) => {
        articleAPI.likeArticle(id, headers)
        .then(() => console.log("Article aimé"))
        .catch((err) => console.log(err))
    }

    handleInfoClick = (id) => {
        this.setState({id, redirect: true})
    }

    handleCommentSubmit = (event, id) => {
        event.preventDefault()

        commentAPI.postComments(id, localStorage.getItem("comment"), headers)
        .then(() => {
            localStorage.removeItem("comment")
