import React, { Component, Fragment } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

// Components
import Navbar from './components/navbar'
import Signup from './components/User/Connexion/Signup';
import Login from './components/User/Connexion/Login';
import Profile from './components/User/Profile'
import Articles from './components/Articles/Articles';
import updateArticle from './components/Articles/updateArticle';
import Comment from './components/Comments/Comment'

import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'


class App extends Component {

  render () {

    return (

      <Fragment>
        <Router>

          <Navbar />


          <Switch>

            <Route exact path='/' component={Login} />
            <Route exact path='/signup' component={Signup} />
            <Route exact path='/profile' component={Profile} />

            <Route exact path='/post' component={Articles} render={(props) => <NewTestComp {...props}/>}/>
            <Route exact path='/post/update/:id' component={updateArticle} />

            <Route exact path='/comment' component={Comment} />

          </Switch>
        </Router>
      </Fragment>
    )
  }
}

export default App;
